import * as React from "react";
import PropTypes from "prop-types";
import Tabs from "@mui/material/Tabs";
import Tab from "@mui/material/Tab";
import Typography from "@mui/material/Typography";
import Box from "@mui/material/Box";
import TabBody from "./TabBody";
import { useEffect } from "react";
import { useState } from "react";

function TabPanel(props) {
  const { children, value, index, ...other } = props;

  return (
    <div
      role="tabpanel"
      hidden={value !== index}
      id={`simple-tabpanel-${index}`}
      aria-labelledby={`simple-tab-${index}`}
      {...other}
    >
      {value === index && (
        <Box sx={{ p: 3 }}>
          <Typography>{children}</Typography>
        </Box>
      )}
    </div>
  );
}

TabPanel.propTypes = {
  children: PropTypes.node,
  index: PropTypes.number.isRequired,
  value: PropTypes.number.isRequired,
};

function a11yProps(index) {
  return {
    id: `simple-tab-${index}`,
    "aria-controls": `simple-tabpanel-${index}`,
  };
}

export default function TabComponent() {
  const [value, setValue] = useState(0);
  const [TotalCount, SetTotalCount] = useState([]);

  const handleChange = (event, newValue) => {
    setValue(newValue);
  };

  const tabArray1 = {
    title: ["Total Assets Dashboard"],
    cardTitle: [
      "Total Asset",
      "Deploy on FLoor",
      "In Stock",
      "Replacement",
      "Damage",
      "Return Back To Vendor",
    ],
    counts: [['7','6','4','8','9']],
  };
  const tabArray2 = {
    title: ["DD Site", "OMR Site", "Mysore Site", "Belgaum Site"],
    cardTitle: [
      "Total Asset",
      "Deploy on FLoor",
      "In Stock",
      "Replacement",
      "Damage",
      "Return Back To Vendor",
    ],

    counts: [
      [1, 2, 3, 4, 5, 6],
      [11, 12, 13, 14, 15, 16],
      [10, 20, 30, 40, 50, 60],
      [7, 8, 9, 10, 11, 12],
    ],
  };
  const tabArray3 = {
    title: [
      "Winger IT Solutions",
      "NXTGEN IT SOLUTION",
      "Sheeltron Digital Systems Pvt.Ltd.",
    ],
    cardTitle: ["Total Asset", "Desktop", "Laptop"],
    counts: [
      [1, 2, 3],
      [4, 5, 6],
      [7, 8, 9],
    ],
  };

  const ApiDataCall = () => {
    // calling api here ..
    fetch("/api/TotalAssets")
    
      .then((res) => res.json())
      .then((data) => {
        if (data.total > 0) {
          SetTotalCount(data.items[0]);
        }
        // console.log("api data", data.items);
      })
      .catch((e) => {
        console.log(e);
      });
  };

  useEffect(() => {
    ApiDataCall();
  }, []);

  return (
    <Box sx={{ width: "100%" }}>
      {console.log(TotalCount)}
      <Box sx={{ borderBottom: 1, borderColor: "divider" }}>
        <Tabs
          value={value}
          onChange={handleChange}
          aria-label="basic tabs example"
        >
          <Tab label="Total Assets dashboard" {...a11yProps(0)} />
          <Tab label="Site Wise Assets Count" {...a11yProps(1)} />
          <Tab label="Vendor Wise Asset Count" {...a11yProps(2)} />
        </Tabs>
      </Box>
      <TabPanel value={value} index={0}>
        <TabBody CardTitleArray={tabArray1} />
      </TabPanel>
      <TabPanel value={value} index={1}>
        <TabBody CardTitleArray={tabArray2} />
      </TabPanel>
      <TabPanel value={value} index={2}>
        <TabBody CardTitleArray={tabArray3} />
      </TabPanel>
    </Box>
  );
}
